import React,{useEffect,useState} from 'react';

function EditTask(props){
    const id=props.id;
    const url='http://localhost:8000/api/get_task/'+id;
    
    
    useEffect(()=>{
        async function AllTaskFetcher(url){
            const response=await fetch(url);
            const json_response=await response.json();
            getTask(json_response);
        };
        AllTaskFetcher(url)
    },[url]);

    const [task,getTask]=useState([]);
    function editTask(url){
        useEffect((url)=>{
            async function AllTaskFetcher(url){
                const response=await fetch(url);
                const json_response=await response.json();
                console.log(json_response);
            };
            AllTaskFetcher(url);
        },[]);  
    };

    const [edit_show,edit_toggel]=useState(false)
    return (
        <div className="edit-task">
            <input type="checkbox" value={task.completed}></input>
            <input type="button" value="edit" onClick={edit_toggel(true)}></input>
            {edit_show?<input type="text"></input>:null}
        </div>
    );
};

export default EditTask;
