import React,{useEffect,useState} from 'react';
import EditTask from './EditTask'
import Fetcher from '../hooks/Fetcher'

function EachTask(props){
    const id=props.id;
    const url='http://localhost:8000/api/get_task/'+id;

    let response,getTask=Fetcher(url);
    let task=response.data;
    let can_edit=response.edit

    return (
        <div  key={task.id} className="task">
            <h1>{task.title}</h1>
            <input type="checkbox" value={task.completed}></input>
            <input type="button" value="edit" onClick={getTask({edit:True})}></input>
            {can_edit?<h1>hi</h1>:null}
        </div>
    );
};

export default EachTask;
