import React,{useEffect,useState} from 'react';
import EditTask from './EditTask'

function EachTask(props){
    const id=props.id;
    const url='http://localhost:8000/api/get_task/'+id;

    useEffect(()=>{
        async function AllTaskFetcher(url){
            const response=await fetch(url);
            const json_response=await response.json();
            getTask(json_response);
        };
        AllTaskFetcher(url)
    },[url]);

    const [task,getTask]=useState([]);

    return (
        <div  key={task.id} className="task">
            <h1>{task.title}</h1>
            <input type="checkbox" value={task.completed}></input>
            <input type="button" onClick={()=>{
                document.querySelector('.edit-task-conatainer').innerHTML="<EditTask />"
            }} value="edit"></input>
            <div className="edit-task-conatainer"></div>
        </div>
    );
};

export default EachTask;
